import org.scalatest.funsuite.AnyFunSuite
import ru.ifmo.backend_2021.markup._

import java.util
import java.util.List


class MarkupTest extends AnyFunSuite {
  test("example test") {
    val paragraph = new Paragraph(util.List.of(
      new Strong(util.List.of(
        new Text("1"),
        new Strikeout(util.List.of(
          new Text("2"),
          new Emphasis(util.List.of(
            new Text("3"),
            new Text("4")
          )),
          new Text("5")
        )),
        new Text("6")
      ))
    ))
    val s = new java.lang.StringBuilder
    assert(paragraph.toMarkdown(s).toString == "__1~2*34*5~6__")
  }

  test("test depth 1") {
    val s = new java.lang.StringBuilder
    val tree = new Paragraph(util.List.of(new Paragraph(util.List.of(new Text("rlg")))))
    assert(tree.toMarkdown(s).toString == "rlg")
  }
  test("test depth 2") {
    val s = new java.lang.StringBuilder
    val tree = new Paragraph(util.List.of(new Strikeout(util.List.of(new Strikeout(util.List.of(new Text("civ"))))),new Strong(util.List.of(new Paragraph(util.List.of(new Text("hha")))))))
    assert(tree.toMarkdown(s).toString == "~~civ~~__hha__")
  }

  test("test depth 3") {
    val s = new java.lang.StringBuilder
    val tree = new Paragraph(util.List.of(new Strong(util.List.of(new Strong(util.List.of(new Strong(util.List.of(new Text("bza"))))))),new Strikeout(util.List.of(new Strikeout(util.List.of(new Strong(util.List.of(new Text("awu")))))))))
    assert(tree.toMarkdown(s).toString == "______bza______~~__awu__~~")
  }

  test("test depth 4") {
    val s = new java.lang.StringBuilder
    val tree = new Paragraph(util.List.of(new Text("qau"),new Strikeout(util.List.of(new Strikeout(util.List.of(new Strong(util.List.of(new Text("ujl"))),new Strikeout(util.List.of(new Strikeout(util.List.of(new Text("qvs"))))))),new Strong(util.List.of(new Text("xfl"))),new Paragraph(util.List.of(new Emphasis(util.List.of(new Paragraph(util.List.of(new Text("dai")))))))))))
    assert(tree.toMarkdown(s).toString == "qau~~__ujl__~~qvs~~~__xfl__*dai*~")
  }

